mapper.select:
SELECT * FROM MAP
WHERE DEL_YN = 0



		AND USER_EMAIL = #{userEmail }



		AND OPEN_YN = #{openYn }

SELECT * FROM MAP
WHERE DEL_YN = 0
AND MAP_IDX = ${mapIdx}

mapper.update:
UPDATE MAKER
SET 

	TITLE= #{title}


	CONTENT = #{content}


	CENTER_LATITUDE = #{centerLatitude}


	CENTER_LOGITUDE = #{centerLogitude}

WHERE DEL_YN = 0
AND MAP_IDX = #{mapIdx}
